package condicionales;

public class EjemploIfElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 18;
		
		if(i == 10) {
			System.out.println("i es 10");
		}else if (i == 15) {
			System.out.println("i es 25");
		}else if (i == 20 ) {
			System.out.println("i es 20");
		}else {
			System.out.println("i no equivale a las concidionales anteriores");
		}
		
		
	}

}
